# Rosalind problem: HAMM
# https://rosalind.info/problems/hamm/

def hamming_distance(sequence1: str, sequence2: str) -> int:
    """Returns the Hamming distance between two sequences."""
    return sum([base1 != base2 for base1, base2 in zip(sequence1, sequence2)])

result = hamming_distance("", "")

print(result)